    <MobileMenuButton>☰</MobileMenuButton>
  </Header>
));

const MemoizedFooter = memo<FooterProps>(({ Logo, FooterText, FooterContent, FooterColumn, FooterHeading, FooterLink, FooterDivider }) => (
  <Footer>
    <FooterContent>
      <FooterColumn>
        <PrefetchLink to="/">
