));

// Optimized image component
const OptimizedImage = memo(({ src, alt, ...props }: { src: string; alt: string; [key: string]: any }) => (
  <img 
    src={src} 
    alt={alt} 
    loading="lazy" 
    decoding="async"
    {...props} 
  />
));

